import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import UseInfo from './UseInfo';

function App() {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [showData, setShowData] =useState(false);
  console.log(firstName,lastName);

  const handleSubmit = () => {
    setShowData(data => {
      console.log('state data', data);
      return data;
    })
  }
  return (
    <div className="App">
      <div className="Form">
      <h2>Registration form</h2><br />
      <label>
          Salutation:
          <select name="salutation">
            <option>--None--</option>
            <option>Mr.</option>
            <option>Ms.</option>
            <option>Mrs.</option>
            <option>Dr.</option>
            <option>Prof.</option>
          </select>
          <br />
        </label>
        <label>First name: <input name="firstName" onChange={(event) => setFirstName(event.target.value)} /></label><br/>
        <label>Last name: <input name="lastName" onChange={(event) => setLastName(event.target.value)} /></label><br />
        <label>Gender :</label>
        <label><input type="radio" name="gender" value="male" /> Male</label>
        <label><input type="radio" name="gender" value="female" /> Female</label><br />
        <label>Email:<input type="email" name="email" /></label><br/>
        <label>Date of Birth:<input type="date" name="birthDate"/></label><br />
        <label>
          Address :</label>
          <textarea name="address" cols="30" rows="3"></textarea><br />
          <button type="submit" onClick={handleSubmit}>Submit</button>
        {
          showData && <UseInfo firstName={firstName} lastName={lastName}></UseInfo>
        }
        </div>
    </div>
  );
}

export default App;
