import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import UseInfo from './UseInfo';

function App() {
  //const [fullName, setFirstName] = useState('');
  const [showData, setShowData] =useState(false);
  const [userData, setUserData] = useState({});
  console.log('userData', userData);

  const handleSubmit = () => {
    setShowData(true);
  };
  const handleUserData = (name, val) => {
    console.log(name, val);
    setUserData(data => {
      console.log('prev data', data);
      return {
        ...data,
        [name]: val
      }
    });
  };
  return (
    <div className="App">
        <h1 style="color:white">Student Registration form</h1>
        <form id="form">
          <div className="form-control">
            <label>Name</label>
            <input type="text" placeholder="Enter your name" onChange={(event) => handleUserData('fullName', event.target.value)} />
          </div>
          <div className="form-control">
            <label>Email</label>
            <input type="email" placeholder="Enter your email" />
          </div>
          <div className="form-control">
            <label>Age</label>
            <input type="text" placeholder="Enter your age" />
          </div>
          <div className="form-control">
            <label>Languages and Frameworks known
              <small>(Check all that apply)</small>
            </label>
            <label><input type="checkbox" name="inp">C</input></label>
            <label><input type="checkbox" name="inp">C++</input></label>
            <label><input type="checkbox" name="inp">C#</input></label>
            <label><input type="checkbox" name="inp">Java</input></label>
            <label><input type="checkbox" name="inp">Python</input></label>
            <label><input type="checkbox" name="inp">JavaScript</input></label>
            <label><input type="checkbox" name="inp">React</input></label>
            <label><input type="checkbox" name="inp">Angular</input></label>  
          </div>
          <div className="form-control">
            <label>Any comments or suggestions</label>
            <textarea placeholder="Enter your comment here"></textarea>
          </div>
          <button onClick={handleSubmit}>Submit</button>
          {
            showData && <UseInfo userData={userData}/>
          }
        </form>
     </div>
  );
}

export default App;
