import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import UseInfo from './UseInfo';

function App() {
  //const [fullName, setFirstName] = useState('');
  const [showData, setShowData] =useState(false);
  const [userData, setUserData] = useState({});
  console.log('userData', userData);

  const handleSubmit = () => {
    setShowData(data => {
      console.log('state data', data);
      return !data;
    })
  this.handleUserData =(name, val) => {
      console.log(name, val);
      setUserData(data =>{
        console.log('prev data', data);
        return{
          ...data,
          [name]:val
        }
      })
    }
  }
  return (
    <div className="App">
        <h1 style="color:white">Student Registration form</h1>
        <form id="form">
          <div class="form-control">
            <label for="name" id="label-name">Name</label>
            <input type="text" placeholder="Enter your name" onChange={(event) => handleUserData('fullName', event.target.value)} />
          </div>
          <div class="form-control">
            <label for="email" id="label-email">Email</label>
            <input type="email" id="email"  placeholder="Enter your email" />
          </div>
          <div class="form-control">
            <label for="age" id="label-age">Age</label>
            <input type="text" id="age" placeholder="Enter your age" />
          </div>
          <div class="form-control">
            <label>Languages and Frameworks known
              <small>(Check all that apply)</small>
            </label>
            <label for="inp-1">
              <input type="checkbox" name="inp">C</input>
            </label>
            <label for="inp-2">
              <input type="checkbox" name="inp">C++</input>
            </label>
            <label for="inp-3">
              <input type="checkbox" name="inp">C#</input>
            </label>
            <label for="inp-4">
              <input type="checkbox" name="inp">Java</input>
            </label>
            <label for="inp-5">
              <input type="checkbox" name="inp">Python</input>
            </label>
            <label for="inp-6">
              <input type="checkbox" name="inp">JavaScript</input>
            </label>
            <label for="inp-7">
              <input type="checkbox" name="inp">React</input>
            </label>
            <label for="inp-7">
              <input type="checkbox" name="inp">Angular</input>
            </label>  
          </div>
          <div class="form-control">
            <label for="comment">Any comments or suggestions</label>
            <textarea name="comment" id="comment" placeholder="Enter your comment here"></textarea>
          </div>
          <button onClick={handleSubmit}>Submit</button>
          {
            showData && <UseInfo userData={userData}/>
          }
        </form>
     </div>
  );
}

export default App;
